INSERT INTO `keke_witkey_article` VALUES ('81', '301', '0', '0', '全款悬赏任务流程', 'help', '0', '0', '<span style=\"font-family:Arial;\">一、雇主发布全款悬赏任务（原威客任务）后，等待其他威客来参加该全款悬赏任务。<br />二、XXX网威客可以通过搜索查看到该全款悬赏任务，并依据任务雇主的需求，提交作品。<br />三、雇主查看到最合适最优秀的作品后，即可将此威客设置为中标者，并为其发放任务赏金后，全款悬赏任务成功结束。</span><br />', '0', '全款悬赏任务流程', '全款悬赏任务流程', '一、雇主发布全款悬赏任务（原威客任务）后，等待其他威客来参加该全款悬赏任务。二、XXX网威客可以通过搜索查看到该全款悬赏任务，并依据任务雇主的需求，提交作品。三、雇主查看到最合适最优秀的作品后，即可将此威客设置为中标者，并为其发放任务赏金后，全款悬赏任务成功结束。', '0', '1', '0', '1322122739', '2');
